exports.main = function(){
    var events = require('events');
    var event = new events.EventEmitter();
    event.on("myevent",function(){
        console.log("myevent 事件触发");
    })

        event.emit("myevent");

    console.log(event.listenerCount());
};
exports.buffer=function(){
    var buf = new Buffer(1024);
    var str = [];
    str[0] = new Buffer("123abc~!@湖北武汉","utf8");
    str[1] = new Buffer("123abc~!@湖北武汉","ascii");
    str[2] = new Buffer("123abc~!@湖北武汉","utf16le");
    str[3] = new Buffer("123abc~!@湖北武汉","ucs2");
    str[4] = new Buffer("123abc~!@湖北武汉","base64");
    for(var i in str){
       console.log(str[i].toString());
   }
    buf.write("中",0,10);
    buf.write("国",10,20);
    console.log(buf.toString());


}


